function numOfHexStr(k) {
  let str = "0123456789ABCDEF";
  return str[k];
}

function Hexofnum(k) {
  if (k === "A" || k === "a") return 10;
  if (k === "B" || k === "b") return 11;
  if (k === "C" || k === "c") return 12;
  if (k === "D" || k === "d") return 13;
  if (k === "E" || k === "e") return 14;
  if (k === "F" || k === "f") return 15;
  return k;
}

